import React from 'react';
import ScrollToBottom from 'react-scroll-to-bottom';

import './Messages.css';

const Messages = (messages,name)=>(
  <ScrollToBottom>
  {messages.map((messages, i)=>
    <div key={i}>
      <Message name={name} message={message} />
    </div>
  )}
  </ScrollToBottom>
)

export default Messages;
